#define OS_Set_PSP_Val(address)      __asm volatile ("mov R0,%[IN] \n\t" "MSR PSP, R0": :[IN] "r"(address))

#define OS_Set_SP_shadowto_PSP       __asm volatile ("MRS R0,CONTROL \n\t" "ORR R0,R0,#0x2 \n\t" "MSR CONTROL, R0")

#define OS_Set_SP_shadowto_MSP     __asm volatile ("MRS R0,CONTROL \n\t" "AND R0,R0,#0x5 \n\t" "MSR CONTROL, R0")

#define OS_Switch_Privileged_to_Unprivileged  __asm volatile ("MRS R0,CONTROL \n\t" "ORR R0,R0,#0x1 \n\t" "MSR CONTROL, R0")


#define OS_Switch_Unprivileged_to_Privileged   __asm volatile ("MRS R0,CONTROL \n\t" "AND R0,R0,#0x6 \n\t" "MSR CONTROL, R0")

