enum CPU_ACCESS_LEVEL
{
	privileged,
	unprivileged
};

void SWITCH_CPU_ACCESS_LEVEL (enum CPU_ACCESS_LEVEL ACCESS_LEVEL){
	switch(ACCESS_LEVEL){
	case privileged:
		__asm("MRS R0,CONTROL\n\t"
		       "LSR R0,R0,#1 \n\t"
			   "LSL R0,R0,#1 \n\t"
			   "MSR CONTROL,R0\n\t");
		break;
	case unprivileged:
		__asm("MRS R0,CONTROL\n\t"
				"ORR R0,R0,#1 \n\t"
				"MSR CONTROL,R0\n\t");
		break;
	}
}
