void main_os(){

	/* OS Stack */
	E_MSP=(S_MSP-main_stack_size);

	/* TaskA Stack */
	S_PSP_TaskA = (E_MSP-8);

	E_PSP_TaskA =(S_PSP_TaskA-Task_A_stack_size);


	/* TaskB Stack */
	S_PSP_TaskB = (E_PSP_TaskA-8);

	E_PSP_TaskB =(S_PSP_TaskB-Task_B_stack_size);




	while(1){

		__asm("NOP");

		if(TaskA_Flag == 1)
		{
			/* Set PSP with _S_PSP_TaskA */
			OS_Set_PSP_Val(S_PSP_TaskA);

			/* Set SP --> PSP not MSP */
			OS_Set_SP_shadowto_PSP;

			/* Switch from privileged --> unprivileged*/
			OS_Switch_Privileged_to_Unprivileged;

			/* Call TaskA */
			TaskA_Flag = TaskA_Fun(1, 2, 3);

			/* Switch from unprivileged --> privileged*/
			OS_Generate_Exception_Inerrupt;

			/* Set SP --> MSP not PSP */
			OS_Set_SP_shadowto_MSP;
		}else if(TaskB_Flag == 1)
		{
			/* Set PSP with _S_PSP_TaskB */
			OS_Set_PSP_Val(S_PSP_TaskB);

			/* Set SP --> PSP not MSP */
			OS_Set_SP_shadowto_PSP;

			/* Switch from privileged --> unprivileged*/
			OS_Switch_Privileged_to_Unprivileged;

			/* Call TaskB */
			TaskB_Flag = TaskB_Fun(1, 2, 3, 4);

			/* Switch from unprivileged --> privileged*/
			OS_Generate_Exception_Inerrupt;

			/* Set SP --> MSP not PSP */
			OS_Set_SP_shadowto_MSP;
		}
		else
		{

		}
	}

}


